import React, { useEffect, useState } from 'react';
import Drink from '../components/DrinkArticle/Drink';
import { getFilterList } from '../hooks/api';

const Api = () => {

  const [dataList, setDataList] = useState([])

  useEffect(() => {
    (async function() {
      try {
        const {data } = await getFilterList();
        if (data && data.drinks) {
          setDataList(data.drinks);
        }
      } catch (e) {
        console.log(e);
      }
    })();
  }, [])

  return (
    <div className='container'>

              {dataList.map(item => (
          <Drink key={item.id} item={item}></Drink>
              ))}
        </div>

  )
}

export {Api};
