import React, { useEffect, useState } from 'react';
import { Link, Route } from 'react-router-dom';
import { getFilterList } from '../hooks/api';
import { List } from './Drinks/List/List';


const Api = () => {

  const [dataList, setDataList] = useState([])

  useEffect(() => {
    (async function() {
      try {
        const {data } = await getFilterList();
        if (data && data.drinks) {
          setDataList(data.drinks);
        }
      } catch (e) {
        console.log(e);
      }
    })();
  }, [])

  return (
    <div className='drinkContainer'>
        <div className='drinkArticles'>
              <Link to="/drinks/Category">Home</Link>
              <button>categories</button>
              <button>glasses</button>
              <button>ingridietns</button>
              <button>alcohol</button>
        </div>
      </div>

  )
}

export {Api};
