import React from 'react';
import useAxios from '../hooks/useAxios';
import axios from '../Apis/DadJokes';

const Api = () => {
  const [joke, error, loading] = useAxios({
    axiosInstance: axios,
    method: 'GET',
    url: '/',
    requstConfig: {
      headers: {
        'content-language': '-RU'
      }
    }
  })

  return (
    <div>
        <article>
          <h2>Random beer</h2>
          {loading && <p>Loading...</p>}
        </article>
    </div>
  )
}

export {Api};
