import React from 'react';
import useAxios from '../hooks/useAxios';
import axios from 'src/Apis/DadJokes.js';

const Beer = () => {
  const [beer, error, loading] = useAxios({
    axiosInstance: axios,
    method: 'GET',
    url: '/',
    requstConfig: {
      headers: {
        'content-language': '-RU'
      }
    }
  })

  return (
    <div>
        <article>
          <h2>Random beer</h2>
          {loading && <p>Loading...</p>}
          {!loading && error && <p>{error}</p>}
        </article>
    </div>
  ) 
}

export default Beer;
