import Menu from './Menu';

export default class Ccomponent extends Component {
    constructor(props) {
      super(props)

      this.state = {
            input: ''
      }

      this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        input: event.target.value
    }
