import React, { useEffect, useState } from 'react';
import { getFilterList } from '../hooks/api';

const Api = () => {

  const [dataList, setDataList] = useState([])

  useEffect(() => {
    (async function() {
      try {
        const {data } = await getFilterList();
        if (data && data.drinks) {
          setDataList(data.drinks);
        }
      } catch (e) {
        console.log(e);
      }
    })();
  }, [])

  return (
    <div className='container'>
        <div className='drinkArticles'>
              {dataList.map(item => (
              <div className='drinkArticle' key={item.strDrink}> 
              <div><h3>{item.strDrink}</h3></div> 
              <img src={item.strDrinkThumb}></img></div>
              ))}
        </div>
      </div>
  )
}

export {Api};
